import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Landing.css';

function Landing() {
  const navigate = useNavigate();

  useEffect(() => {
    document.body.classList.add('landing-page');
    
    return () => {
      document.body.classList.remove('landing-page');
    };
  }, []);

  const handleUserLogin = () => {
    navigate('/user/login');
  };

  const handleUserRegister = () => {
    navigate('/user/register');
  };

  const handleAdminLogin = () => {
    navigate('/admin/login');
  };

  return (
    <div className="landing-container">
      <div className="landing-content">
        <div className="landing-header">
          <h1>Barangay Mangan-vaca Job Portal</h1>
          <p>Connecting opportunities within our community</p>
        </div>
        
        <div className="access-containers">
          <div className="access-container user-container">
            <div className="container-icon">üë•</div>
            <h3>Job Seekers & Employers</h3>
            <p>Find opportunities and connect with local talent</p>
            <div className="landing-buttons">
              <button 
                className="landing-btn user-login"
                onClick={handleUserLogin}
              >
                User Login
              </button>
              <button 
                className="landing-btn register"
                onClick={handleUserRegister}
              >
                Register
              </button>
            </div>
          </div>

          <div className="access-container admin-container">
            <div className="container-icon">üõ°Ô∏è</div>
            <h3>System Administrators</h3>
            <p>Manage the job portal and oversee operations</p>
            <div className="landing-buttons">
              <button 
                className="landing-btn admin-login"
                onClick={handleAdminLogin}
              >
                Admin Login
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Landing;
