import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Login.css';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      console.log('Attempting login for:', email);
      
      // Try user login first
      const userRes = await fetch('http://localhost:5000/api/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password }),
      });

      const userData = await userRes.json();
      console.log('Login response:', userData);

      if (userRes.ok && userData.success) {
        // Store user information
        localStorage.setItem('isLoggedIn', 'true');
        localStorage.setItem('user', JSON.stringify(userData.user));
        
        // Role-based redirection
        if (userData.user.isAdmin) {
          localStorage.setItem('userRole', 'admin');
          // Redirect to admin dashboard
          window.location.href = 'http://localhost:3001'; // Admin app URL
          return;
        } else {
          localStorage.setItem('userRole', 'user');
          // Redirect to user dashboard
          navigate('/home');
          return;
        }
      } else {
        // Show specific error message
        alert(userData.message || 'Login failed. Please check your credentials.');
      }
    } catch (error) {
      console.error('Login error:', error);
      alert('Login failed. Please try again.');
    }
  };

  return (
    <div className="login-container">
      <div className="login-background-circle"></div>
      <div className="login-logo-circle">
        <img 
          src={process.env.PUBLIC_URL + '/barangay_logo.png'} 
          alt="Barangay Logo" 
        />
      </div>
      
      <div className="login-decorative-shapes">
        <div className="shape shape-1"></div>
        <div className="shape shape-2"></div>
        <div className="shape shape-3"></div>
      </div>

      <form className="login-form" onSubmit={handleSubmit}>
        <h2>Login to Mangan-Vaca Job Portal</h2>
        <div className="login-field">
          <label htmlFor="email">Email</label>
          <input
            type="email"
            id="email"
            placeholder="Enter your email"
            value={email}
            autoComplete="username"
            onChange={e => setEmail(e.target.value)}
            required
          />
        </div>
        <div className="login-field">
          <label htmlFor="password">Password</label>
          <div className="login-password-wrapper">
            <input
              type={showPassword ? "text" : "password"}
              id="password"
              placeholder="Enter your password"
              value={password}
              autoComplete="current-password"
              onChange={e => setPassword(e.target.value)}
              required
            />
            <button
              type="button"
              className="login-showpass-btn"
              onClick={() => setShowPassword(!showPassword)}
              tabIndex={-1}
            >
              {showPassword ? "Hide" : "Show"}
            </button>
          </div>
        </div>
        <button className="login-btn" type="submit">Login</button>
        <div className="login-footer">
          <span>Don't have an account?</span>
          <a href="/register">Register</a>
        </div>
      </form>
    </div>
  );
}

export default Login;
